mapper.select:
select i.* from t_identity_info i inner join t_company_info c on i.company_id=c.id


        and c.id=#{search.companyId}


        and c.society_code=#{search.societyCode}


        and i.batch_id=#{search.batchId}

select i.accept_number,i.id_number,i.name,i.reservation_date, i.sex,o.culture_degree,p.profession_type,p.job_type, c.company_name,c.company_mobile,o.self_phone,p.certificate_code, p.issuing_authority,p.issuing_date from t_identity_info i left join t_house_other o on i.id=o.identity_info_id left join t_house_profession p on i.id=p.identity_info_id left join t_company_info c on i.company_id=c.id
i.police_approve_status= 3 and i.renshe_accept_status = 3
and i.batch_id=#{item.batchId}
and i.id_number=#{item.idNumber}
and i.ACCEPT_ADDRESS_ID=#{item.acceptAddressId}

select t1.PREAPPROVE, t1.name SPname, t1.ID_NUMBER SPID_NUMBER, t2.name, t2.id_number, t2.relationship, t3.operator, t3.idcardnumber_1, t3.operator2, t3.idcardnumber_2 from t_identity_info t1 left join t_house_relationship t2 on t1.id = t2.identity_info_id left join t_company_info t3 on t1.company_id=t3.id
(t2.relationship = '配偶' or t2.relationship is null) and t1.UNION_APPROVE_STATUS_2=1 and t1.police_approve_status= 3 and t1.renshe_accept_status = 3
and t1.batch_id=#{item.batchId}
and t1.ACCEPT_ADDRESS_ID=1
and t1.ACCEPT_ADDRESS_ID=2
and to_char(t1.preApprove,'yyyy-MM-dd')=#{item.preApprove}

select t1.name PERSONNAME, t1.id_number PERSONIDNUM, t1.reservation_date RESERVATIONDATE, case when t2.profession_type = 1 then '无' when t2.profession_type = 2 then '具有专业技术人员职业资格' when t2.profession_type = 3 then '具有技能人员职业资格' end PROFESSIONTYPE from t_identity_info t1, t_house_profession t2
t1.id = t2.identity_info_id and t1.batch_id in (select id from t_batch_conf where status=1) and t1.accept_address_id = 1
and t1.name=#{item.personName}
and t1.id_number=#{item.personIdNum}
and t2.profession_type=#{item.professionType}
and to_char(t1.reservation_date,'yyyy-MM-dd')=#{item.reservationDate}
order by t1.reservation_date asc

select i.accept_number,o.applicant_type,i.id_number,i.name, p.profession_type,p.job_type,o.profession, i.region,i.hall_status,i.reservation_status,m.move_registered_office from t_identity_info i left join t_house_other o on i.id=o.identity_info_id left join t_house_profession p on i.id=p.identity_info_id left join t_house_move m on i.id=m.identity_info_id
i.police_approve_status= 3 and i.renshe_accept_status = 3
and i.batch_id=#{item.batchId}
and i.id_number=#{item.idNumber}
and i.ACCEPT_ADDRESS_ID=#{item.acceptAddressId}

select
company_id
from t_identity_info


        and batch_id=#{item.batchId}


group by company_id having count(1)>=#{limit}

select r.ID , r.ACCEPT_NUMBER , r.BATCH_ID , r.INDICATOR_ID , r.INDICATOR_NAME , r.PERSON_ID , r.PERSON_NAME , r.PERSON_ID_NUM , r.PERSON_MOBILE_PHONE , r.COMPANY_ID , r.COMPANY_NAME , r.STATUS , round(r.SCORE_VALUE,2) SCORE_VALUE, r.ITEM_ID , r.ACCEPT_DATE , r.SUBMIT_DATE , r.SCORE_DATE , r.OP_USER_ID , r.OP_USER , r.OP_ROLE_ID , r.OP_ROLE , r.SCORE_DETAIL , r.C_TIME , r.ACCEPT_ADDRESS_ID , r.ORIGINAL_SCORE_VALUE , r.ISDEDUCTED , r.TOREVIEWREASON , r.TOREVIEWTIME , r.IDREVIEWEND ,c.batch_name,i.accept_address from t_pb_score_record r left join t_identity_info i on r.person_id = i.id left join t_batch_conf c on r.batch_id=c.id
i.police_approve_status= 3 and i.renshe_accept_status = 3
and r.batch_id = #{item.batchId}
and r.person_name like concat('%',concat(#{item.personName}, '%'))
and r.person_id_num like concat('%',concat(#{item.personIdNum}, '%'))
and r.company_name like concat('%',concat(#{item.companyName}, '%'))
and r.indicator_name like concat('%',concat(#{item.indicatorName}, '%'))
and r.status = #{item.status}
and i.accept_address_id = #{item.acceptAddressId}
and TO_CHAR(r.accept_date,'yyyy-mm-dd') >= #{item.acceptDateBegin}
and TO_CHAR(r.accept_date,'yyyy-mm-dd') <= #{item.acceptDateEnd}
and TO_CHAR(r.submit_date,'yyyy-mm-dd') >= #{item.submitDateBegin}
and TO_CHAR(r.submit_date,'yyyy-mm-dd') <= #{item.submitDateEnd}
and TO_CHAR(r.score_date,'yyyy-mm-dd') >= #{item.scoreDateBegin}
and TO_CHAR(r.score_date,'yyyy-mm-dd') <= #{item.scoreDateEnd}

select i.accept_number, i.id_number, i.name, o.self_phone, i.sex, i.opuser4, o.culture_degree, p.job_title_level, p.job_name, p.job_level, p.profession_title, p.profession_type, p.job_type, p.certificate_code, p.issuing_authority, p.issuing_date, c.company_name, c.company_mobile, c.operator, c.operator_mobile, r.name partner_name, r.id_number partner_id_number, sr.status_time accept_date, th.COMPANY_NAME COMPANY_NAME_other, th.APPLY_OFFICE_ADDRESS from t_identity_info i left join t_house_other o on i.id=o.identity_info_id left join t_house_profession p on i.id=p.identity_info_id left join t_company_info c on i.company_id=c.id LEFT join t_house_other th on th.IDENTITY_INFO_ID=i.id left join t_house_relationship r on i.id=r.identity_info_id and r.relationship='配偶' left join t_pb_status_record sr on i.id=sr.person_id and status_dict_alias='hallStatus' and status_int=5
i.police_approve_status= 3
and i.batch_id = #{item.batchId} and i.reservation_status = 11 and i.renshe_accept_status = 3
and i.accept_address_id = #{item.acceptAddressId}
and i.name like concat('%',concat(#{item.personName}, '%'))
and i.id_number like concat('%',concat(#{item.personIdNum}, '%'))
and c.company_name like concat('%',concat(#{item.companyName}, '%'))
and TO_CHAR(sr.status_time,'yyyy-mm-dd') >= #{item.acceptDateBegin}
and TO_CHAR(sr.status_time,'yyyy-mm-dd') <= #{item.acceptDateEnd}
order by i.accept_number asc
